ALTER TABLE "projects_t" ADD COLUMN "ts" tsvector generated always as (setweight(to_tsvector((case when lang = 'fr' then 'french'::regconfig when lang = 'en' then 'english'::regconfig end), coalesce(title, '')), 'A') || setweight(to_tsvector((case when lang = 'fr' then 'french'::regconfig when lang = 'en' then 'english'::regconfig end), coalesce(summary, '')), 'B') || setweight(to_tsvector((case when lang = 'fr' then 'french'::regconfig when lang = 'en' then 'english'::regconfig end), coalesce(description, '')), 'C')) stored;